-- Creating notifications reference tables

CREATE TABLE CGP_REF_SUBSCRIBERS
(
  ID NUMBER(19) PRIMARY KEY NOT NULL,
  SUBSCRIBER_EMAIL VARCHAR2(255) NOT NULL,
  FIRST_NAME VARCHAR2(255),
  LAST_NAME VARCHAR2(255),
  DESCRIPTION VARCHAR2(255)
);
CREATE INDEX IDX_SUBSCRIBERS ON CGP_REF_SUBSCRIBERS (SUBSCRIBER_EMAIL);

CREATE TABLE CGP_REF_SUBSCRIBER_NOTIF
(
  NOTIFICATION_ID NUMBER(19) NOT NULL,
  SUBSCRIBER_ID NUMBER(19) NOT NULL
);
CREATE UNIQUE INDEX IDX_SUBSCRIBER_NOTIFICATION ON CGP_REF_SUBSCRIBER_NOTIF (NOTIFICATION_ID, SUBSCRIBER_ID);


CREATE TABLE CGP_REF_NOTIFICATIONS
(
  ID NUMBER(19) PRIMARY KEY NOT NULL,
  CATEGORY VARCHAR2(255) NOT NULL,
  SUBCATEGORY VARCHAR2(255),
  SUBCATEGORY_DESC VARCHAR2(255)
);
CREATE INDEX IDX_NOTIFICATIONS ON CGP_REF_NOTIFICATIONS (SUBCATEGORY);

